mapper.select:
SELECT * FROM t_user
WHERE email = #{email}
  AND provider = #{provider}
  AND authCd IS NULL

SELECT * FROM t_user
WHERE iuser = #{iuser}
  AND authCd IS NULL

mapper.update:
UPDATE t_user
SET authCd = null
WHERE email = #{email} AND authCd = #{authCd}

UPDATE t_user
SET mainProfile = #{mainProfile}
WHERE iuser = ${iuser}

UPDATE t_user


        mainProfile = #{mainProfile}


        pw = #{pw}


        nm = #{nm}


        nick = #{nick}


        gender = #{gender}


        birthY = #{birthY}


        birthM = #{birthM}


        birthD = #{birthD}


        tel = #{tel}


WHERE iuser = #{iuser}
